questionvariables.text:
/* Pool of rational expressions (true) */
rationals_all: [
    5, -4, 7/3, 0.25, 3/10, sqrt(25), sqrt(49),
    12/5, 0.12121212, -9, 4/7, 0, -13, 1.5,
    2/3, sqrt(36), sqrt(100), 0.33333333
];

/* Pool of irrational expressions (false) */
irrationals_all: [
    pi, sqrt(2), sqrt(3), sqrt(5), 2*sqrt(2),
    pi - 3, sqrt(8), 0.121221222, 3*sqrt(7),
    sqrt(10), e, sqrt(11), pi/2, sqrt(13)
];

/* Select correct answers (rational numbers) */
