questionvariables.text:
ta:random_permutation([["Triangle PQR and ABC are similar", true],["Triangle PQR and ABC are not similar", false]]);
/*random letters*/
letter1:rand([a,b,c,d,g,h,j,k,m,n]);
letter2:rand([p,q,r,s,t,u,v,w,x,y,z]);

/*scale factor*/
k:rand([1.5,2,2.5,3,3.5,4]);
/*length of the lines of  triangle PQR*/
PQ:rand(5)+2;
PR:rand_with_step(8,12,1);
QR:rand_with_prohib(5,12,[PQ,PR]);

/*length of the lines of  triangle ABC*/
AB:k*PQ;
BC:k*QR;
AC:k*PR;
ta2:QR;
ta1:AC;
