questionvariables.text:
/****************************************************************/
stack_include_contrib("prooflib.mac");

/* Random scale factor */
scale_factor : 2 + rand(2);   /* picks either 2 or 3 */

/* Base triangle coordinates (randomised but structured) */
xA : 0;  
yA : 0;

xB : 2 + rand(3);   /* between 2 and 4 */
yB : 0;

xC : 1 + rand(3);   /* between 1 and 3 */
yC : 1 + rand(3);   /* between 1 and 3 */

/* Image triangle after enlargement from A */
xB1 : scale_factor*xB;
yB1 : scale_factor*yB;

xC1 : scale_factor*xC;
yC1 : scale_factor*yC;


/* Find extremes of all points (original + image) */
xmin : min(xA, xB, xC, xB1, xC1) - 1;
xmax : max(xA, xB, xC, xB1, xC1) + 1;
ymin : min(yA, yB, yC, yB1, yC1) - 1;
ymax : max(yA, yB, yC, yB1, yC1) + 1;

/* -------------------------------*/
/****************************************************************/
/* Theorem statement */
thm: "Show that \\(\\triangle ABC \\sim \\triangle AB'C'\\).";

/****************************************************************/
/* Proof steps */ 
proof_steps: [
  ["assumption", "Consider \\(\\triangle ABC\\) and its image \\(\\triangle AB'C'\\) under an enlargement with centre \\(A\\) and scale factor \\(k\\)."],

  ["step1", "Point \\(A\\) is the centre of enlargement, hence \\(A = A'\\)."],

  ["step2", "By definition of enlargement, \\(AB' = k\\cdot AB\\) and \\(AC' = k\\cdot AC\\)."],

  ["step3", "Thus the corresponding side lengths are in proportion: \\(\\tfrac{AB'}{AB} = \\tfrac{AC'}{AC} = k\\)."],

  ["step4", "An enlargement preserves angles at the centre, so \\(\\angle BAC = \\angle B'AC'\\)."],

  ["step5", "Therefore, two pairs of sides are in the same ratio and the included angle is equal (SAS criterion)."],

  ["conclusion", "Hence, \\(\\triangle ABC \\sim \\triangle AB'C'\\)."]
];

/****************************************************************/
/* Correct proof order */
proof_ans: proof("assumption",
    proof_iff("step1","step2"),
    proof_iff("step3","step4"),
    "step5",
    "conclusion"
);


/* Randomize steps for Parsons-style task */
proof_steps: random_permutation(proof_steps);
