questionvariables.text:
/* Randomised variables */

/* Surface areas*/
SA_P : 10*rand_with_step(5,12,1);              /* total surface area of cylinder P (e.g. 50–120 cm²) */
scale_factor_area : rand([4,9,16,25]);  /* area scale factor between 4 and 25 */
SA_Q : SA_P * scale_factor_area;

/* Linear scale factor */
scale_factor_length : sqrt(scale_factor_area);

/* Lengths */
L_P : rand_with_step(3,6,1);                   /* length of cylinder P (3–6 cm) */
L_Q : L_P * scale_factor_length;



/* Volumes */
V_P : 10*rand_with_step(8,15,1);               /*volume of cylinder P (80–150 cm³) */
scale_factor_volume : scale_factor_length^3;
V_Q : V_P * scale_factor_volume;

/* Answers */
ta_ans1 : decimalplaces(L_Q,2);    /* length of Q */
ta_ans2 : decimalplaces(V_Q,2);    /* volume of Q */
