questionvariables.text:
/* Base triangle (kept central) */
Ax: -3;
Ay: 1;

Bx: -1;
By: 4;

Cx: 2;
Cy: 2;

/* Random translation (small, safe) */
tx: rand(4) + 3;   /* 3 to 6 */
ty: rand(4) - 2;   /* -2 to 1 */

/* Rotation choice */
rot: rand(2);  /* 0 or 1 */

/* Image triangle */
Sx: if rot=0 then Ax+tx else -Ax+tx;
Sy: if rot=0 then Ay+ty else -Ay+ty;

Tx: if rot=0 then Bx+tx else -Bx+tx;
Ty: if rot=0 then By+ty else -By+ty;

Vx: if rot=0 then Cx+tx else -Cx+tx;
Vy: if rot=0 then Cy+ty else -Cy+ty;
/* Normalise student answer: yes/no -> 1/0 */
ans1n :
  if is(ans1 = "yes") or is(ans1 = "Yes") then 1
  elseif is(ans1 = "no") or is(ans1 = "No") then 0
  else -1;


