questionvariables.text:
/* Randomize side (even numbers between 12 m and 24 m) */
side : 2 * (rand(59) + 6);

/* Randomize height (even numbers less than side, between 4 m and side - 2) */
height : 2 * (rand(floor(side/2 - 1)) + 2);

/* Calculate area of one triangle */
triangle_area : (1/2) * side * height;

/* Calculate total area of the pentagon */
total_area : 5 * triangle_area;
