questionvariables.text:
/* Randomize the sector angle between 60° and 180° in steps of 10° */
theta: rand_with_step(60, 270, 10);

/* Randomize the outer radius (longer radius) between 10 and 25 cm */
Wiper: rand_with_step(7,47,7);
R: rand_with_step(7, 140, 7)+Wiper;
r:R-Wiper;

/* Define pi */
Pi: 22/7;

/* Area of the sector (shaded region) */
R_1:R^2;
r_1:r^2;
Radius_difference:R_1-r_1;
A:(theta/360) *  Pi * (R^2 - r^2);
A_1:float(A);

/* Round to 3 significant figures for display */
A_3sf: decimalplaces(A,3);
