questionvariables.text:
/* Step 1: Randomize radius and angle */
radius : rand_with_step(7,140,7);        /* radius between 10 and 20 */
theta_deg : rand_with_step(10,180,10);   /* angle between 60° and 135° */
R:radius^2;

/* Step 2: Area of sector */
area_sector : theta_deg/360*22/7*radius^2;
Area_sector:decimalplaces(area_sector,4);
/* Step 3: Area of triangle (1/2 ab sinθ)
   where a = b = radius */
degree:R*float(sin(theta_deg * %pi / 180));

area_triangle : 1/2*radius^2*float(sin(theta_deg * %pi / 180));
Area_triangle:decimalplaces(area_triangle,4);

/* Step 4: Area of segment */
area_segment :area_sector-area_triangle;
Area_segment:decimalplaces(area_segment,4);
