questionvariables.text:
/* Randomised radius (km) */
r : rand_with_step(100,700,10);

/* Distance between centres (km), must be < 2r */
d :rand_with_step(500,5000,100);

/* Central angle (degrees) subtended by the overlap chord */
theta_deg : rand_with_step(60,180,10);


/*Area of the segment*/
sector:theta_deg/360*22/7*r^2;

triangle:1/2*r^2*float(sin(theta_deg * %pi / 180));
/*Area of the first segment */
seg:sector-triangle;
/*The area of the overlapping circle */
Overlaping_area:2*seg;
Area:round(Overlaping_area);
