questionvariables.text:
/* Randomize one side of the square base */
a : rand_with_step(4, 14, 4);     /* base side length in cm */

/* Half of base side */
half_a : a / 2;

/* Randomize slant height L such that L > half_a */
L : half_a + rand_with_step(2, 30, 2);   /* ensures L > half_a */

/* Use Pythagoras to find vertical height h */
h : sqrt(L^2 - half_a^2);

/* Base area */
Base_area : a^2;

/* Volume of the pyramid */
V : (1/3) * Base_area * h;

/* Convert to decimal and round */
height_val : float(h);
Height_pyramid : decimalplaces(height_val, 4);
height_pyramid:round(Height_pyramid);

volume_val : float(V);
Volume_pyramid : decimalplaces(volume_val, 4);
volume_pyramid:round(Volume_pyramid);
