questionvariables.text:
/* Randomize the length of the cylinder (h) */
h : rand_with_step(50, 200, 5);    /* Length between 50 m and 200 m */

/* Randomize inner radius (r) */
r : rand_with_step(5, 15, 1);      /* Inner radius between 5 m and 15 m */

/* Randomize outer radius (R) ensuring it is greater than inner radius */
R : r + rand_with_step(15, 60, 5);  /* Outer radius = inner radius + 1 to 10 m */

/* Use pi = 3.142 */
pi_val : 3.142;

/* Volume of hollow cylinder: V = pi*(R^2 - r^2)*h */

/*Volume of the Outer cylinder*/
V_Outer:pi_val*R^2*h;


/*Volume of the inner cylinder*/
V_Inner:pi_val*r^2*h;
V_1:round(V_Outer-V_Inner);

/* Volume of hollow cylinder: V = pi*(R^2 - r^2)*h */
V : pi_val * (R^2 - r^2) * h;
