questionvariables.text:
/* Randomise diameter and height */
diameter : 6*rand_with_step(2,40,2);   /* diameter between 10 cm and 49 cm */
height   : rand_with_step(5,100,5);   /* height between 10 cm and 49 cm */

/* Radius */
r : diameter / 2;

/* Use pi = 22/7 */
pi_val : 22/7;

/* Volume of the cylinder */
V : pi_val * r^2 * height;
volume:float(V);
Volume_cylinder:decimalplaces(volume,2);
