questionvariables.text:
/* Randomize diameter of the cylinder */
d_cyl : rand_with_step(6, 20, 2);   /* cylinder diameter between 6 cm and 20 cm */

/* Randomize diameter of the sphere (always greater than cylinder) */
d_sph : d_cyl + rand_with_step(10, 40, 2);  /* sphere diameter > cylinder diameter */

/* Heights and radii */
r_sph : d_sph / 2;
r_cyl : d_cyl / 2;

/* Fraction of sphere filled with water */
fraction : 4/3;

/* Use pi */
pi_val : 22/7;

/* Volume of water from sphere */
V_sphere : fraction * (4/3) * pi_val * r_sph^3;

/* Depth of water in the cylinder */
h : V_sphere / (pi_val * r_cyl^2);

/* Convert to decimal and round */
depth : float(h);
Depth_cylinder : decimalplaces(depth, 2);
