questionvariables.text:
/* List of Pythagorean triples (radius, height, slant height) */
triples : [
  [3,4,5],
  [5,12,13],
  [6,8,10],
  [7,24,25],
  [8,15,17],
  [9,12,15],
  [10,24,26],
  [12,16,20],
  [12,35,37],
  [15,20,25],
  [16,30,34],
  [18,24,30],
  [20,21,29]
];

/* Randomly select one triple */
T : rand(length(triples)) + 1;

/* Assign cone dimensions in cm */
r_cm : triples[T][1];   /* radius */
h_cm : triples[T][2];   /* perpendicular height */
l_cm : triples[T][3];   /* slant height */


/* Area of the circular base */
base_area : 22/7 * r_cm^2;
B_H:base_area*h_cm;

/* Volume of the cone */
ta : (1/3) * base_area * h_cm;
ta_1:round(ta);
