/* Step 2: Randomly pick one */
chosen : triples[rand(40) + 1];
b : chosen[1]; /* base */
h : chosen[2]; /* height */
hyp : chosen[3]; /* hypotenuse */

/* Step 3: Ensure the length is always greater than both */
l : max(b, h) + rand(6) + 6; /* example: if max = 24, l could be 30 */
