questionvariables.text:
/*The triplets of the Triangle.*/
list_of_the_values:[[3,4,5],[6,8,10],[9,12,15],[5, 12, 13],[7, 24, 25],[8, 15, 17],
    [9, 40, 41],[11, 60, 61],[12, 35, 37],[13, 84, 85],[16, 63, 65],[20, 21, 29],[33, 56, 65],
    [36, 77, 85],[39, 80, 89],[48, 55, 73],[60, 91, 109],[65, 72, 97],[80, 99, 121],[15, 20, 25],
    [35, 72, 85],[36, 77, 85],[39, 80, 89],[72, 24, 72]];
index:1+rand(24);
choose_one:list_of_the_values[index];
/*The */
base_radius_of_the_cone:choose_one[1];
height_of_the_cone:choose_one[2];
slantheight_of_the_cone:choose_one[3];
/*Computing the total surface area of the cone.*/
R_times_l:base_radius_of_the_cone*slantheight_of_the_cone;

/*Curved surface area of the cone*/
Area:22/7 *R_times_l;
area_1:float(Area);
Area_cone:decimalplaces(area_1,3);

/*Curved surface area of the hemisphere*/
R1:base_radius_of_the_cone^2;
Hemisphere_area:2*22/7*base_radius_of_the_cone^2;
Hemishere_Area:float(Hemisphere_area);

/*Total external surface area*/
SA:area_1+Hemishere_Area;
Surface_Area:round(SA);
