questionvariables.text:
/* 1. SCENARIO & CORRECT ANSWER SETUP */
/* NEW Scenarios focusing on different contexts (Agriculture, Wildlife, Business, etc.) */
scenarios: [
  ["A wildlife ranger hides in a blind to count the number of elephants visiting a watering hole at sunset without disturbing them.", "Observation"],

  ["A mobile phone company invites a small group of 8 teenagers to have a guided discussion about what features they dislike in current smartphones.", "Focus Group"],

  ["The government requires every single citizen to provide details on their age, occupation, and housing status to assist in national planning.", "Census"],

  ["A farmer divides his land into two plots. He uses a new fertilizer on Plot A and the old fertilizer on Plot B to see which one produces more maize.", "Experimentation"],

  ["A university sends an email form to all 10,000 students asking them to rate the quality of the cafeteria food on a scale of 1 to 5.", "Questionnaire"],

  ["An economist analyzes the inflation rates in Kenya from 1990 to 2020 using annual reports published by the National Bureau of Statistics.", "Secondary Data Collection"]
];

/* Pick a random scenario */
rand_idx: rand(length(scenarios)) + 1;
chosen_scenario: scenarios[rand_idx];

q_text: chosen_scenario[1];
correct_text: chosen_scenario[2];

/* 2. DISTRACTOR SETUP */
/* Master list of all possible methods */
all_methods: [
    "Observation", 
    "Focus Group", 
    "Census", 
    "Experimentation", 
    "Questionnaire", 
    "Secondary Data Collection", 
    "Face-to-Face Interview", 
    "Telephone Survey", 
    "Case Study"
];

/* Filter out the correct answer */
wrong_pool: sublist(all_methods, lambda([x], x # correct_text));

/* Shuffle the wrong pool */
shuffled_wrongs: random_permutation(wrong_pool);

/* 3. BUILD THE ANSWER LIST */
/* Correct Answer: ID 1 */
correct_answer: rand_selection([
   [1, true, correct_text]
], 1);

/* Incorrect Answers: ID 2-4 (Pick 3 distinct wrong ones) */
incorrect_answers: rand_selection([
   [2, false, shuffled_wrongs[1]],
   [3, false, shuffled_wrongs[2]],
   [4, false, shuffled_wrongs[3]]
], 3);

/* 4. FINAL PERMUTATION */
ta: random_permutation(append(correct_answer, incorrect_answers));

/* 5. INPUT HELPER */
final_options: map(third, ta);
