questionvariables.text:
/* 1. SCENARIO & CORRECT ANSWER SETUP */
/* Define the scenarios and their matching correct method */
scenarios: [
  ["You stand in a corner and record how long customers spend in each aisle without interfering.", "Observation"],
  ["You gather detailed opinions and feelings from a small group of 10 people regarding a new product.", "Focus Group"],
  ["You collect data from every single household in the entire country.", "Census"],
  ["You treat one plot with fertilizer and leave another without to compare the growth results.", "Experimentation"],
  ["You survey 500 employees anonymously using a standard form to gather data quickly.", "Questionnaire"],
  ["You use existing rainfall records from the Meteorological Department for your research.", "Secondary Data Collection"]
];

/* Pick a random scenario */
rand_idx: rand(length(scenarios)) + 1;
chosen_scenario: scenarios[rand_idx];

q_text: chosen_scenario[1];
correct_text: chosen_scenario[2];

/* 2. DISTRACTOR SETUP */
/* Master list of all possible methods */
all_methods: [
    "Observation", 
    "Focus Group", 
    "Census", 
    "Experimentation", 
    "Questionnaire", 
    "Secondary Data Collection", 
    "Face-to-Face Interview", 
    "Telephone Survey", 
    "Case Study"
];

/* Filter out the correct answer to ensure distractors are actually wrong */
wrong_pool: sublist(all_methods, lambda([x], x # correct_text));

/* Shuffle the wrong pool so we get random distractors every time */
shuffled_wrongs: random_permutation(wrong_pool);

/* 3. BUILD THE ANSWER LIST (Matching your sample structure) */

/* Correct Answer Block: ID 1, Boolean TRUE, Text */
correct_answer: rand_selection([
   [1, true, correct_text]
], 1);

/* Incorrect Answers Block: ID 2-4, Boolean FALSE, Text (picked from shuffled pool) */
incorrect_answers: rand_selection([
   [2, false, shuffled_wrongs[1]],
   [3, false, shuffled_wrongs[2]],
   [4, false, shuffled_wrongs[3]]
], 3);

/* 4. FINAL PERMUTATION */
/* Combine and shuffle to get the final list 'ta' */
ta: random_permutation(append(correct_answer, incorrect_answers));

/* 5. INPUT HELPER */
/* Extract just the text strings for the STACK Radio Button list */
/* 'third' takes the 3rd element of each sublist (the text) */
final_options: map(third, ta);
