questionvariables.text:
/* 1. DATA GENERATION */
/* We generate 30 values. To ensure repetition (for the Mode), we limit the pool of numbers. */
/* We pick numbers between 450 and 550, but only multiples of 5 (e.g. 450, 455, 460...) */
/* This small pool guarantees that in 30 draws, numbers will repeat multiple times. */
pool: makelist(450 + 5*k, k, 0, 20); 
raw_data: makelist(random_permutation(pool)[1], i, 1, 30);

N: length(raw_data);

/* 2. MEAN CALCULATION */
sum_x: apply("+", raw_data);
mean_val: float(sum_x / N);


/* 3. MEDIAN CALCULATION */
sorted_data: sort(raw_data);
/* Since N=30 (even), Median is average of 15th and 16th terms */
term15: sorted_data[15];
term16: sorted_data[16];
median_val: float((term15 + term16) / 2);


/* 4. MODE CALCULATION (Robust Logic) */
/* Get unique elements */
unique_vals: unique(raw_data);
/* Count frequency of each unique element */
freqs: map(lambda([x], length(sublist(raw_data, lambda([y], y=x)))), unique_vals);
/* Find maximum frequency */
max_freq: lmax(freqs);
/* Find all values that match this max frequency */
mode_indices: sublist_indices(freqs, lambda([x], x=max_freq));
modes: map(lambda([idx], unique_vals[idx]), mode_indices);

/* Format the mode answer */
/* If one mode, answer is the number. If multiple, answer is a set {a,b} */
ta_mode: if length(modes)=1 then modes[1] else setify(modes);

/* Display string for the raw data (comma separated) */
data_str: sconcat(raw_data);



/* --- NEW: Generate Frequency Table for Feedback --- */
/* 1. Get unique values and sort them */
u_vals: sort(unique(raw_data));

/* 2. Count frequency for each unique value */
u_freqs: map(lambda([x], length(sublist(raw_data, lambda([y], y=x)))), u_vals);

/* 3. Build HTML rows string */
freq_table_rows: "";
for i:1 thru length(u_vals) do (
    /* Highlight the row if it is the mode */
    row_style: if u_freqs[i] = max_freq then "background-color: #d4edda; font-weight: bold;" else "",

    freq_table_rows: sconcat(
        freq_table_rows,
        "<tr style='", row_style, "'><td>", u_vals[i], "</td><td>", u_freqs[i], "</td></tr>"
    )
);
