questionvariables.text:
/* 1. SETUP INTERVALS (Months 0-12) */
/* Unequal widths to force use of Frequency Density */
x0: 0;
x1: 2;   /* Jan-Feb (2 months) */
x2: 5;   /* Mar-May (3 months) */
x3: 6;   /* Jun (1 month - wet season start) */
x4: 9;   /* Jul-Sep (3 months) */
x5: 12;  /* Oct-Dec (3 months) */

w1: x1 - x0;
w2: x2 - x1;
w3: x3 - x2;
w4: x4 - x3;
w5: x5 - x4;

/* 2. RANDOMIZE HEIGHTS (Frequency Density) */
/* Using integers 1-9 for clear y-axis readability */
h1: rand([2,3]);
h2: rand([1,2]);
h3: rand([7,8,9]); /* The "Monsoon" peak */
h4: rand([4,5,6]);
h5: rand([2,3]);

/* 3. CALCULATE FREQUENCIES (Total Rainfall per interval) */
/* Area = Width * Height */
freq1: w1 * h1;
freq2: w2 * h2;
freq3: w3 * h3;
freq4: w4 * h4;
freq5: w5 * h5;

total_rain: freq1 + freq2 + freq3 + freq4 + freq5;

/* 4. DEFINE TEACHER ANSWERS (ta) */

/* Q1: Rain in Monsoon month (Interval 3: x2 to x3) */
ta1: freq3;

/* Q2: Estimate cumulative rain up to T = 7.5 months */
T_cut: 7.5;
/* Sum of full bars before interval 4 */
rain_prev: freq1 + freq2 + freq3;
/* Partial bar calculation for interval 4 */
p_width: T_cut - x3;
rain_partial: p_width * h4;
ta2: rain_prev + rain_partial;

/* Q3: Percentage of total rain in the Monsoon month */
ta3: (freq3 / total_rain) * 100;

/* Q4: Mean Monthly Rainfall */
ta4: total_rain / 12;

/* Formatting for display (rounded) */
ta3_disp: float(round(ta3*100)/100);
ta4_disp: float(round(ta4*100)/100);
