questionvariables.text:
/* Random mantissas suitable for log tables */
A: rand(900) + 100;   /* 100–999 */
B: rand(900) + 100;   /* 100–999 */

/* Convert to decimals with 3 significant figures */
A: A / 100;
B: B / 100;

/* Random powers of 10 */
pa: rand(3);  /* 0,1,2 */
pb: rand(3);

/* Final numbers shown to students */
a: A * 10^pa;
b: B * 10^pb;

/* Logarithms rounded to 4-figure table accuracy (teacher-side only) */
loga: round(log(a)/log(10)*10000)/10000;
logb: round(log(b)/log(10)*10000)/10000;

/* Sum of logarithms */
logsum: loga + logb;

/* Exact product */
ta_exact: a * b;

/* Table-style final answer (3 significant figures) */
ta: significantfigures(ta_exact, 3);

/* Tolerance for logarithm table rounding (±2%) */
tol: abs(ta) * 0.02;
/* PRT helper: added numbers instead of using logarithms */
wrong_add_numbers: a + b;

/* PRT helper: multiplied logarithms instead of adding (safe) */
wrong_mult_logs: exp(float(round(loga * logb)) * float(log(10)));

/* PRT helper: mantissas only (ignored powers of ten) — safe */
mant_a: round(float((loga - floor(loga))*10000)/10000);
mant_b: round(float((logb - floor(logb))*10000)/10000);
wrong_mant_only: exp(float((mant_a + mant_b) * log(10)));
