questionvariables.text:
/* 1. Generate the Frequency components (1.000 to 9.999) */
d1 : rand(8) + 1;       /* Integer part: 1-9 (Avoid 0) */
d2 : rand(10);          /* 1st decimal: 0-9 */
d3 : rand(10);          /* 2nd decimal: 0-9 */
d4 : rand(9) + 1;       /* 3rd decimal (mean difference): 1-9 */

/* Construct the Frequency 'f' */
freq : d1 + d2/10 + d3/100 + d4/1000;
freq : float(freq);

/* 2. Simulate the Table Lookup Values */
/* 'part_a' is the Row (e.g., 2.3) */
part_a : d1 + d2/10;

/* 'part_b' is the Main Column (e.g., 4) */
part_b : d3;

/* 'part_c' is the Difference Column (e.g., 7) */
part_c : d4;

/* 3. Calculate the Table Values */
/* Calculate the value found in the main body (1 / d1.d2d3) rounded to 4dp */
val_lookup_base : d1 + d2/10 + d3/100;
table_val_main : decimalplaces(1/val_lookup_base, 4);

/* Calculate the exact final answer (1/f) */
ta : decimalplaces(1/freq, 4);

/* Calculate the 'Subtract' value (Mean Difference) */
/* We reverse-engineer the difference to ensure the feedback math works perfectly */
/* Difference = (Main Table Value) - (True Answer) */
diff_val : table_val_main - ta;

/* Convert this to the integer seen in the "Subtract" column (e.g., 0.0005 -> 5) */
sub_num : round(diff_val * 10000);

/* Safety: Ensure sub_num is positive (Reciprocals always decrease) */
sub_num : abs(sub_num);

/* Formatting for display */
/* Ensures 4.500 displays as 4.500 not 4.5 in some contexts if needed */
freq_disp : dispdp(freq, 3);
