questionvariables.text:
/* 1. Generate the decimal number components (1.000 to 9.999) */
/* We generate digits separately to ensure clean table lookups */
d1 : rand(9) + 1;       /* Integer part: 1-9 */
d2 : rand(10);          /* 1st decimal: 0-9 */
d3 : rand(10);          /* 2nd decimal: 0-9 */
d4 : rand(9) + 1;       /* 3rd decimal (mean difference): 1-9 */

/* Construct the full number 'a' */
a : d1 + d2/10 + d3/100 + d4/1000;
a : float(a);

/* 2. Simulate the Table Lookup Values */
/* 'part_a' is the Row (e.g., 2.3) */
part_a : d1 + d2/10;

/* 'part_b' is the Main Column (e.g., 4) */
part_b : d3;

/* 'part_c' is the Difference Column (e.g., 7) */
part_c : d4;

/* Calculate the Main Table Value (First 3 digits) */
/* In a table of reciprocals, we look up 1 / (d1.d2d3) */
val_lookup : d1 + d2/10 + d3/100;
a_1_answer : decimalplaces(1/val_lookup, 4);

/* 3. Calculate Final Answer and Difference */
/* 'ta' is the true reciprocal rounded to 4 decimal places */
ta : decimalplaces(1/a, 4);

/* Calculate the 'Subtract' value (Mean Difference) */
/* Since 1/x is a DECREASING function, the table subtracts the difference. */
/* We find the integer difference between the main value and the final answer */
/* Multiply by 10000 to get the integer represented in the 'Subtract' column */
sub_num : round((a_1_answer - ta) * 10000);

/* Safety check: ensure sub_num is positive (it should be, as 1/x decreases) */
sub_num : abs(sub_num);

/* Format variables for display */
part_a_disp : ssubst("", "0.", string(part_a)); /* Helps formatting if needed */
