questionvariables.text:
dp(num, dp) := block(if num*10^dp-floor(num*10^dp)<0.5 then return(float(floor(num*10^dp)/(10^dp))) else return(float(ceiling(num*10^dp)/(10^dp))));

lenRat:rand([12,14,15,16,18])/10;

/* Randomise similar lengths */

redHeight:rand_with_prohib(41,79,[50,60,70])/10;
greenHeight:redHeight*lenRat;

/* Randomise areas and volumes */

redArea: if lenRat=3/2 then 6*rand_with_step(11,19,1)/10 else 15*rand_with_step(3,7,1)/10;
greenArea:redArea*lenRat^2;
redVol:redArea*redHeight/3;
greenVol:greenArea*greenHeight/3;

feedbackvariables.text:
/* Node 1: Correct answer */
/* Node 2: Worked solution */

/* Node 3: Uses inverse of correct ratio (1) */
node3_test: if abs(ans2-greenArea*lenRat^2)<=0.1 then 1 else 0;

/* Node 4: Does not square ratio (1) */
node4_test: if abs(ans2-greenArea/lenRat)<=0.1 then 1 else 0;

/* Node 5: N3,4 errors combined (feedback) */
node5_test: if abs(ans2-greenArea*lenRat)<=0.1 then 1 else 0;

/* Node 1: Correct answer */
/* Node 2: Worked solution */

/* Node 3: Correct answer, rounded incorrectly (2) */
node3_test: if abs(ans3-greenVol)<=0.1 then 1 else 0;

/* Node 4: Uses inverse of correct ratio (1) */
node4_test: if abs(ans3-redVol/lenRat^3)<=0.1 then 1 else 0;

/* Node 5: Does not cube ratio (1) */
node5_test: if abs(ans3-redVol*lenRat)<=0.1 or abs(ans3-redVol*lenRat^2)<=0.1 then 1 else 0;

/* Node 6: N4,5 errors combined (feedback) */
node6_test: if abs(ans3-redVol/lenRat)<=0.1 or abs(ans3-redVol/lenRat^2)<=0.1 then 1 else 0;
