questionvariables.text:
/* List of shapes:
   1 = square
   2 = rectangle (non-square)
   3 = rhombus (non-square)
   4 = kite
   5 = equilateral triangle
   6 = regular pentagon
   7 = cross-shaped dodecagon
*/

shape_id: rand(7) + 1;

/* Order of rotational symmetry for each shape */
order_sym:
  if shape_id = 1 then 4
  elseif shape_id = 2 then 2
  elseif shape_id = 3 then 2
  elseif shape_id = 4 then 1
  elseif shape_id = 5 then 3
  elseif shape_id = 6 then 5
  elseif shape_id = 7 then 4;

/* Human-readable name (for feedback only) */
shape_name:
  if shape_id = 1 then "square"
  elseif shape_id = 2 then "rectangle"
  elseif shape_id = 3 then "rhombus"
  elseif shape_id = 4 then "kite"
  elseif shape_id = 5 then "equilateral triangle"
  elseif shape_id = 6 then "regular pentagon"
  elseif shape_id = 7 then "dodecagon";

/* Random rotation so image is not static */
k: rand(4);
rot_js: float(k * %pi/2);

/* Teacher's answer */
ta: order_sym;
