questionvariables.text:
/* Title: Random Pentagon Area Calculator with Expanded Range (NO conditionals) */

/* Base length: divisible by 10, between 20 and 260 */
base_length : 10 * (rand(25) + 2);

/* Half base */
half_base : base_length / 2;

/* Maximum slant height: always safely greater than half_base */
max_slant : max(base_length - 5, half_base + 5);

/* Slant height: divisible by 5, strictly between half_base and max_slant */
slant : 5 * (
  floor(half_base/5)
  + rand(floor((max_slant - half_base)/5))
  + 1
);

/* Height using Pythagoras */
height : sqrt(slant^2 - half_base^2);
height_d : decimalplaces(float(height), 1);

/* Area of one triangle */
area_triangle : (1/2) * base_length * height_d;
area_triangle_d : decimalplaces(area_triangle, 1);

/* Area of pentagon */
area_pentagon : 5 * area_triangle;
ta_exact : decimalplaces(area_pentagon, 1);
ta : round(area_pentagon);

/* ---------- Randomised mistake generation (NO conditionals) ---------- */

/* Mistake selector */
mistake_number : rand(4) + 1;

/* List of mistake answers */
mistake_answers : [
  round((1/2) * base_length * slant),      /* using slant as height */
  round((1/2) * base_length * half_base),  /* using half base as height */
  round(area_triangle),                    /* forgetting ×5 */
  round((base_length * slant) / 2)         /* base × slant / 2 */
];

/* Corresponding feedback messages */
mistake_feedback : [
  "Great effort! Remember, the slant height is not the perpendicular height. Use Pythagoras to find the correct height.",
  "Nice try. Half the base helps form the right triangle, but it is not the perpendicular height.",
  "Almost there! You found the area of one triangle. Remember to multiply by 5.",
  "Good attempt! The slant height is not perpendicular to the base. Use the perpendicular height in the area formula."
];

/* Selected mistake answer and feedback */
mistake_answer : mistake_answers[mistake_number];
feedback : mistake_feedback[mistake_number];
