questionvariables.text:
/* Title: Random Pentagon Area Calculator with Expanded Range */

/* Generate random base_length divisible by 10 between 20 and 200 (20 options) */
base_length : 10 * (rand(20) + 2); /* random(19)+2 ensures minimum 20 */

/* Calculate maximum possible slant height (less than base_length, greater than half_base) */
half_base : base_length / 2;
max_slant : base_length - 5; /* ensure slant < base_length */

/* If half_base is greater than or equal to max_slant, adjust max_slant to be at least half_base + 5 */
if half_base >= max_slant then max_slant : half_base + 5;

/* Generate random slant height divisible by 5, greater than half_base but less than base_length */
slant : 5 * (floor(half_base/5) + rand(floor((max_slant - half_base)/5)) + 1);

slant1:slant^2;
half_base1:half_base^2;
height_1:slant1-half_base1;

/* Calculate height using Pythagoras theorem */
height : sqrt(height_1);
height3:float(height);
height2:decimalplaces(height3,1);


/* Calculate area of one triangle */
area_1:base_length *height2;
area_triangle : (1/2) * base_length * height2;
area_triangle1:decimalplaces(area_triangle,1);

/* Calculate area of pentagon */
area_pentagon : 5 * area_triangle;
ta1:decimalplaces(area_pentagon,1);
ta:round(area_pentagon );

/* ---------- Randomized mistake generation ---------- */

/* Generate random mistake number between 1 and 4 */
mistake_number : rand(4) + 1;

/* Define mistake answers */
if mistake_number = 1 then (
    mistake_answer : round((1/2) * base_length * slant), /* using slant as height */
    feedback : "Great effort! Remember, slant height is the side of the triangle, not its perpendicular height. Try using Pythagoras to find the height correctly."
)
else if mistake_number = 2 then (
    mistake_answer : round((1/2) * base_length * half_base), /* using half base as height */
    feedback : "Nice try. Half the base helps form the right-angled triangle, but it is not the perpendicular height itself. Please review the triangle splitting method."
)
else if mistake_number = 3 then (
    mistake_answer : round(area_triangle), /* forgetting to multiply by 5 */
    feedback : "Almost there! You calculated the area of one triangle perfectly. Just remember to multiply by 5 to get the total pentagon area."
)
else if mistake_number = 4 then (
    mistake_answer : round((base_length * slant) / 2), /* using (base*slant)/2 directly */
    feedback : "You’re working hard, which is great! However, slant height is not perpendicular to the base. Always use the perpendicular height in the area formula."
);

