questionvariables.text:
/* Step 1: Choose a Pythagorean triplet
   XN = a , ON = b , XO = c */
triplets : [
  [3,4,5],
  [4,3,5],
  [5,12,13],
  [12,5,13],
  [6,8,10],
  [8,6,10],
  [8,15,17],
  [15,8,17],
  [9,12,15],
  [12,9,15],
  [12,16,20],
  [16,12,20],
  [5,12,13],
  [6,8,10],
  [8,15,17]
];
T : rand(length(triplets)) + 1;
[a,b,c] : triplets[T];

XN : a;
ON : b;
XO : c;

/* Step 2: Chord length */
XY : 2*XN;

/* Step 3: Angle NOX (right triangle XNO)
   Use cosine (can also use sine or tangent) */

angle_NOX : acos(ON/XO)* 180/%pi, numer;

/* Step 4: Central angle XOY */
angle_XOY : 2*angle_NOX;

/* Step 5: Areas */
area_sector1 : angle_XOY/360*22/7*XO^2;
area_sector:decimalplaces(area_sector1,3);
area_triangle : 1/2*(XY*ON);

area_minor_segment1 : area_sector1 - area_triangle;
area_minor_segment:decimalplaces(area_minor_segment1,3);

area_circle1:22/7*XO^2;
area_circle:decimalplaces(area_circle1,3);

area_major_segment1 : area_circle - area_minor_segment1;
area_major_segment:decimalplaces(area_major_segment1,3);
