questionvariables.text:
/* Random radius and height in cm */
r_cm : rand_with_step(84,168,7) + 5;     /* radius between 5 and 10 cm */
h_cm : rand_with_step(36,72,3) + 10;   /* height between 10 and 20 cm */

/* Convert to metres */
r : r_cm / 100;
r_1:float(r);
h : h_cm / 100;
h_1:float(h);

base:22/7*r_1^2;
B_h:base*h_1;

/* Volume of the cone in cubic metres */
ta : (1/3)*22/7*r_1^2*h_1;
