questionvariables.text:
/* 1. Define Starting Point M */
mx: rand_with_step(-5, 5, 1);
my: rand_with_step(-5, 5, 1);

/* 2. Define First Translation Vector v1 */
v1x: rand_with_step(2, 6, 1);
v1y: rand_with_step(2, 6, 1);
/* Randomly negate some values for variety */
if rand([true,false]) then v1x: -v1x;
if rand([true,false]) then v1y: -v1y;

/* 3. Define Second Translation Vector v2 */
v2x: rand_with_step(2, 6, 1);
v2y: rand_with_step(2, 6, 1);
if rand([true,false]) then v2x: -v2x;
if rand([true,false]) then v2y: -v2y;

/* --- CALCULATIONS --- */

/* Part (a): Coordinates of M' */
/* M' = M + v1 */
m1x: mx + v1x;
m1y: my + v1y;
/* We store the answer as a column matrix to match standard vector input */
ans_a: matrix([m1x],[m1y]);

/* Part (b): Coordinates of M'' */
/* M'' = M' + v2 */
m2x: m1x + v2x;
m2y: m1y + v2y;
ans_b: matrix([m2x],[m2y]);

/* Part (c): Single Translation Vector */
/* V_total = v1 + v2 */
vtx: v1x + v2x;
vty: v1y + v2y;
ans_c: matrix([vtx],[vty]);

feedbackvariables.text:
correct_answer : if ans1=ans_a  and ans2=ans_b  and ans3=ans_c then 1 else 0;

/* --- COMMON MISTAKES --- */

/* Mistake (a): Subtracted vector instead of added */
/* Student calculates M - v1 */
wa_a: matrix([mx - v1x], [my - v1y]);

/* Mistake (b): Translated from original M instead of M' */
/* Student calculates M + v2 (ignoring the first step) */
wa_b: matrix([mx + v2x], [my + v2y]);

/* Mistake (c): Reverse direction */
/* Student calculates vector from M'' to M (signs flipped) */
wa_c: matrix([-vtx], [-vty]);
