questiontext.text:
Move \(p_3\) and \(p_4\) so that the vector \(\vec{p_3p_4}\) is identical to the vector \(\vec{p_1p_2}\).

[[jsxgraph input-ref-ans1='ans1Ref']]

var board = JXG.JSXGraph.initBoard(divid, { boundingbox : [-5.5, 5.5, 5.5, -5.5], axis : true });

var p1 = board.create('point', {#p1#}, { name : 'p_1', fixed: true });
var p2 = board.create('point', {#p2#}, { name : 'p_2', fixed: true, lastArrow: {type: 2, size: 8, highlightSize: 6} });

var p3 = board.create('point', {#p3#}, { name : 'p_3', fixed: false, snapSizeX: 0.5, snapSizeY: 0.5, snapToGrid: true });
var p4 = board.create('point', {#p4#}, { name : 'p_4', fixed: false, lastArrow: {type: 2, size: 8, highlightSize: 6}, snapSizeX: 0.5, snapSizeY: 0.5, snapToGrid: true });

board.create('arrow', [p1, p2], { fixed : true, strokeWidth : 4, strokeOpacity : .6 });
board.create('arrow', [p3, p4], {strokeWidth : 4, strokeOpacity : .6 });

stack_jxg.bind_point_relative(ans1Ref, p3, p4);

[[/jsxgraph]]

<p style="display:none">[[input:ans1]] [[validation:ans1]]</p>
[[feedback:prt1]]

questionvariables.text:
p1x: rand_with_step(-5, 0, 1);
p1y: rand_with_step(-5, 0, 1);
p2x: p1x+rand_with_step(1, 4, 1);
p2y: p1y+rand_with_step(1, 5, 1);
v1: [p2x-p1x, p2y-p1y];

p3x: rand_with_step(0, 5, 1);
p3y: rand_with_step(-5, 0, 1);
p4x: p3x+rand_with_step(-5, -1, 1);
p4y: p3y+rand_with_step(1, 5, 1);

p1: [p1x, p1y];
p2: [p2x, p2y];
p3: [p3x, p3y];
p4: [p4x, p4y];

ta1: [[p2x+1, p2y], v1]

feedbackvariables.text:
sv: ans1[2];
stack: matrix(sv, v1);
is_parallel: is(rank(stack) = 1);
