questionvariables.text:
/* 1. Generate Random Coordinates (Integers) */
/* Range -5 to 5 ensures they fit well on a -6 to 6 grid */
ax: rand_with_step(-5, 5, 1);
ay: rand_with_step(-5, 5, 1);

bx: rand_with_step(-5, 5, 1);
by: rand_with_step(-5, 5, 1);

/* Safety check: ensure points are distinct */
bx: if (bx=ax and by=ay) then bx+2 else bx;

/* 2. Define Answers as MATRICES */
ta_pos_a: matrix([ax], [ay]);
ta_pos_b: matrix([bx], [by]);

/* Vector AB = b - a */
vec_ab_x: bx - ax;
vec_ab_y: by - ay;
ta_vec_ab: matrix([vec_ab_x], [vec_ab_y]);

/* 3. Wrong Answers for Feedback */
wa_rev: matrix([ax - bx], [ay - by]);
wa_sum: matrix([ax + bx], [ay + by]);
