questionvariables.text:
/* 1. SCENARIO & CORRECT ANSWER SETUP */
/* Define the scenarios (Questions) and their matching correct answer */
scenarios: [
  ["Which of the following best describes a <b>position vector</b>?", "A vector which always starts at the origin of the coordinate system."],
  ["Select the definition that correctly describes a <b>position vector</b>.", "A vector which always starts at the origin of the coordinate system."]
];

/* Pick a random scenario (in case you want to vary the wording) */
rand_idx: rand(length(scenarios)) + 1;
chosen_scenario: scenarios[rand_idx];

q_text: chosen_scenario[1];
correct_text: chosen_scenario[2];

/* 2. DISTRACTOR SETUP */
/* Master list of all possible vector definitions (Correct + Distractors) */
all_defs: [
    "A vector which always starts at the origin of the coordinate system.", 
    "A vector which may be translated to any point in space provided their direction and length remain unchanged.", 
    "A vector whose length may change as long as its direction remains the same.", 
    "A vector whose direction may change as long as its length stays the same.",
    "A vector having magnitude zero and undefined direction."
];

/* Filter out the correct answer to ensure distractors are actually wrong */
/* This ensures 'correct_text' is never in the 'wrong_pool' */
wrong_pool: sublist(all_defs, lambda([x], x # correct_text));

/* Shuffle the wrong pool so we get random distractors every time */
shuffled_wrongs: random_permutation(wrong_pool);

/* 3. BUILD THE ANSWER LIST */

/* Correct Answer Block: ID 1, Boolean TRUE, Text */
correct_answer: rand_selection([
   [1, true, correct_text]
], 1);

/* Incorrect Answers Block: ID 2-4, Boolean FALSE, Text (picked from shuffled pool) */
/* We pick the top 3 from the shuffled list to serve as distractors */
incorrect_answers: rand_selection([
   [2, false, shuffled_wrongs[1]],
   [3, false, shuffled_wrongs[2]],
   [4, false, shuffled_wrongs[3]]
], 3);

/* 4. FINAL PERMUTATION */
/* Combine correct and incorrect blocks and shuffle them into the final list 'ta' */
ta: random_permutation(append(correct_answer, incorrect_answers));

/* 5. INPUT HELPER */
/* Extract just the text strings for the STACK Radio Button list display */
final_options: map(third, ta);
