questionvariables.text:
/* Randomised parameters */
v : rand(7) + 3;          /* velocity between 3 and 9 m/s */
T : rand(6) + 6;          /* total time between 6 and 11 s */
t0 : rand(T-2) + 2;       /* query time strictly inside interval */

/* Displacement function */
s(t) := v*t;

/* Teacher's answer */
ta : s(t0);
