questionvariables.text:
/* Generate 5-digit random number */
seven_digit: rand(90000) + 10000;

/* Even/Odd */
is_even: mod(seven_digit,2)=0;

/* Prime check */
is_prime(n) := if n<2 then false
               else if n=2 then true
               else if mod(n,2)=0 then false
               else block([limit:floor(sqrt(n)),i:3,result:true],
                   while i<=limit and result do (
                     if mod(n,i)=0 then result:false,
                     i:i+2
                   ),
                   result
               );
is_prime_num: is_prime(seven_digit);

/* Labels */
option_labels: ["it is even","it is odd","it is prime","it is composite"];
option_flags: [is_even, not is_even, is_prime_num, (not is_prime_num and seven_digit>1)];

/* Teacher answer (correct labels) */
t_ans1: [];
for i:1 thru length(option_labels) do (
  if option_flags[i] then t_ans1: append(t_ans1,[option_labels[i]])
);


perm: random_permutation(makelist(i,i,1,length(option_labels)));
ta: makelist([ option_labels[perm[i]], option_labels[perm[i]] ], i, 1, length(option_labels));

/* Composite factor for feedback */
composite_factor: if not is_prime_num and seven_digit>1 then
  block([f:0],
    if mod(seven_digit,2)=0 then f:2
    else if mod(seven_digit,3)=0 then f:3
    else for k:5 step 2 while k*k<=seven_digit and f=0 do (
      if mod(seven_digit,k)=0 then f:k
    ),
    f
  )
else 0;

/* Build correct answer display string */
correct_answers_str: sconcat("<p>", stringjoin(correct_answers, "<br>"), "</p>");
