questionvariables.text:
/* Random parameters */
start_x: rand([-3, -2]);
start_y: rand([-1, 1, 2]);
length: rand([4, 5]);
height: rand([3, 4, 5]);

/* Base L-shape (teacher shape) */
base_shape: [
  [start_x, start_y],
  [start_x + length, start_y],
  [start_x + length - 1, start_y + 1],
  [start_x + 1, start_y + 1],
  [start_x + 1, start_y + height],
  [start_x, start_y + height]
];

/* Reflection across the y-axis */
reflected_shape: map(lambda([p], [-p[1], p[2]]), base_shape);

/* Completely unrelated student starting shape */
student_shape: [[4,1],[5,1],[6,3],[6,4],[5,6],[4,6]];
