questionvariables.text:
/* Initial position */
initCen:[rand([1,-1])*rand([2,3,4]), rand([1,-1])*rand([2,3,4])];
initTipOffset:random_permutation([0,rand([1,-1])*2]);
initTip:[first(initCen)+first(initTipOffset), second(initCen)+second(initTipOffset)];

/* Reflected shape */
cenTrans:rand_with_prohib(4, min(abs(first(initCen)),abs(second(initCen)))+4, [abs(first(initCen))+abs(second(initCen))]);
refCen:[first(initCen)+(if first(initCen)>=0 then -1 else 1)*cenTrans, second(initCen)+(if second(initCen)>=0 then -1 else 1)*cenTrans];

/* Finding line of reflection */
cenMid: [(first(initCen)+first(refCen))/2,(second(initCen)+second(refCen))/2];
tans1: if sign(first(initCen))=sign(second(initCen)) then y=first(cenMid)+second(cenMid)-x else y=x+second(cenMid)-first(cenMid);

/* Random variables for (b) */
refAxis:rand(["x","y"]);
refCenB: if refAxis="x" then [first(initCen), -second(initCen)] else [-first(initCen), second(initCen)];
transCenB: [(if first(refCenB)>=0 then 1 else -1)*rand_with_prohib(2,4,[abs(first(refCenB))]), (if second(refCenB)>=0 then 1 else -1)*rand_with_prohib(2,4,[abs(second(refCenB))])];
transVec:matrix([first(transCenB)-first(refCenB)],[second(transCenB)-second(refCenB)]);

/* Solving for rotation point for (b) */
tans3: if refAxis="x" then [first(first(transVec))+first(initTip), first(second(transVec))-second(initTip)] else [first(first(transVec))-first(initTip), first(second(transVec))+second(initTip)];

feedbackvariables.text:
/* Node 1: Check correct answer */
/* Node 2: Worked solution */
/* Node 3: Correct coefficient of x (+1) */
node3_test: if abs((coeff(lhs(ans1)-rhs(ans1),x)/coeff(lhs(ans1)-rhs(ans1),y))-(coeff(lhs(tans1)-rhs(tans1),x)/coeff(lhs(tans1)-rhs(tans1),y)))<=0.0001 then 1 else 0;

/* Node 4: Correct y-intercept (+1) */
node4_test: if abs((coeff(lhs(ans1)-rhs(ans1),x,0)/coeff(lhs(ans1)-rhs(ans1),y))-(coeff(lhs(tans1)-rhs(tans1),x,0)/coeff(lhs(tans1)-rhs(tans1),y)))<=0.0001 then 1 else 0;

/* Node 1: Check correct answer */
node1_test: if ans2=transCenB and first(ans3)-first(tans3)<=0.1 and second(ans3)-second(tans3)<=0.1 then 1 else 0;

/* Node 2: Worked solution */

/* Node 3: Correct centre of arrow with incorrect orientation (2) */
node3_test: if ans2=transCenB then 1 else 0;

/* Node 4: Reflects in the wrong axis (1) */

wrongAxisCen: if refAxis="y" then [first(initCen)+first(first(transVec)), first(second(transVec))-second(initCen)] else [first(first(transVec))-first(initCen), first(second(transVec))+second(initCen)];

wrongAxisTip: if refAxis="y" then [first(initTip)+first(first(transVec)), first(second(transVec))-second(initTip)] else [first(first(transVec))-first(initTip), first(second(transVec))+second(initTip)];

node4_test: if ans2=wrongAxisCen and first(ans3)-first(wrongAxisTip)<=0.1 and second(ans3)-second(wrongAxisTip)<=0.1 then 1 else 0;

/* Node 5: Wrong order of transformations (1) */

unordCen: if refAxis="x" then [first(initCen)+first(first(transVec)), -first(second(transVec))-second(initCen)] else [-first(first(transVec))-first(initCen), first(second(transVec))+second(initCen)];

unordTip: if refAxis="x" then [first(initTip)+first(first(transVec)), -first(second(transVec))-second(initTip)] else [-first(first(transVec))-first(initTip), first(second(transVec))+second(initTip)];

node5_test: if ans2=unordCen and first(ans3)-first(unordTip)<=0.1 and second(ans3)-second(unordTip)<=0.1 then 1 else 0;

/* Node 6: Correct reflection followed by attempt to translate (uses (2,-2) or similar) (1) */

impliedVec: matrix([first(ans2)-first(refCenB)], [second(ans2)-second(refCenB)]);

node6_test: if ((refAxis="x" and abs(first(ans3)-first(first(impliedVec))-first(initTip))<=0.1 and abs(second(ans3)-first(second(impliedVec))+second(initTip))<=0.1) or (refAxis="y" and abs(first(ans3)-first(first(impliedVec))+first(initTip))<=0.1 and abs(second(ans3)-first(second(impliedVec))-second(initTip))<=0.1)) and {abs(first(first(impliedVec))), abs(first(second(impliedVec)))}={abs(first(first(transVec))), abs(first(second(transVec)))} then 1 else 0;

/* Node 7: Translates or reflects only (feedback) */

node7_test: if (ans2=refCenB and abs(first(ans3)-first(tans3)+first(first(transVec)))<=0.1 and abs(second(ans3)-second(tans3)+first(second(transVec)))<=0.1) or (first(ans2)=first(initCen)+first(first(transVec)) and second(ans2)=second(initCen)+first(second(transVec)) and abs(first(ans3)-first(initTip)-first(first(transVec)))<=0.1 and abs(second(ans3)-second(initTip)-first(second(transVec)))<=0.1) then 1 else 0;

/* Node 8: Transforms green shape (feedback) */

refCenG: if refAxis="x" then [first(first(transVec))+first(refCen), first(second(transVec))-second(refCen)] else [first(first(transVec))-first(refCen), first(second(transVec))+second(refCen)];

node8_test: if ans2=refCenG then 1 else 0;
