questionvariables.text:
/* Randomize the vertical distance from 5 to 25 cm in steps of 1 */
d: rand_with_step(1,56,4);

/* Angle of 45 degrees */
theta: 45;

/* Convert degrees to radians for computation */
theta_rad: theta * %pi/180;

/* Length of one arm of the divider */
arm_length: d / sin(theta_rad);
