questionvariables.text:
/* Randomize base of the triangle (b) */
b : rand_with_step(4, 24, 2);  /* base between 4 cm and 14 cm in steps of 2 */

/* Randomize height of the triangle (h_tri) */
h_tri : rand_with_step(3, 20, 1);  /* triangle height between 3 cm and 10 cm */

/* Randomize vertical height of the pyramid (H) */
H : rand_with_step(5, 30, 1);   /* pyramid height between 5 cm and 20 cm */

/* Compute base area of the triangle */
Base_area : (1/2) * b * h_tri;

/* Compute volume of the pyramid */
V : (1/3) * Base_area * H;

/* Convert to float and round to 2 decimal places */
volume : float(V);
Volume_pyramid : decimalplaces(volume, 2);
