questionvariables.text:
/* Randomize the radius (r) */
r : rand_with_step(7, 84, 7);  /* radius between 1 cm and 10 cm */
R:r^3;
/* Use pi = 22/7 */
pi_val : 22/7;

/* Compute volume of the sphere: V = 4/3 * pi * r^3 */
v:pi_val * r^3;
V : (4/3) * pi_val * r^3;

/* Convert to float and round to 2 decimal places */
volume : float(V);
Volume_sphere : decimalplaces(volume, 3);
