questionvariables.text:
/* Step 1: Load 40 predefined Pythagorean triples */
triples: [
  [3,4,5],[5,12,13],[6,8,10],[7,24,25],[8,15,17],
  [9,12,15],[9,40,41],[10,24,26],[11,60,61],[12,16,20],
  [12,35,37],[13,84,85],[14,48,50],[15,20,25],[15,36,39],
  [16,30,34],[16,63,65],[18,24,30],[18,80,82],[20,21,29],
  [20,48,52],[21,28,35],[21,72,75],[24,32,40],[24,45,51],
  [27,36,45],[28,45,53],[30,40,50],[30,72,78],[33,44,55],
  [33,56,65],[35,84,91],[36,48,60],[36,77,85],[39,52,65],
  [40,75,85],[45,60,75],[48,55,73],[60,63,87],[65,72,97]
];

/* Step 2: Randomly pick one triple */
chosen : triples[rand(40) + 1];
b : chosen[1];     /* base of right triangle */
h : chosen[2];     /* height of triangle */
hyp : chosen[3];   /* sloping side of triangle */

/* Step 3: The big base of the tent = 2 * b */
big_base : 2 * b;

/* Step 4: Choose the tent length (ensure it is longer than triangle sides) */
l : max(b, h) + rand(35) + 6;

/* Step 5: Compute triangle area */
area_triangle : (1/2) * big_base * h;

/* Step 6: Compute area of rectangle (use hypotenuse and length) */
area_rectangle : hyp * l;

/* Step 7: Total Surface Area of Tent Canvas (using your given formula) */
total_SA : 2 * area_triangle + 2 * area_rectangle;

/* Step 8: Final answer as integer (no decimal places) */
final_answer : floor(total_SA);

/* Step 9: Display values */
[b, h, hyp, big_base, l, total_SA, final_answer];
