questionvariables.text:
/* Randomize radius and height around 40 (Â±5) */
r: rand_with_step(7,77,7); /* radius between 35 and 45 cm */
h: 35 + rand(11); /* height between 35 and 45 cm */

/* Step 1: Curved surface area of cylinder */
R_H:r*h;
CSA_cyl: 2*22/7*r*h;
CSA_cyl_val: float(CSA_cyl);

/* Step 2: Curved surface area of hemisphere */
R:r^2;
CSA_hemi: 2*22/7*r^2;
CSA_hemi_val: float(CSA_hemi);

/* Step 3: Total external surface area */
Total_SA: CSA_cyl + CSA_hemi;
Total_SA_val: float(Total_SA);

/* Step 4: Volume of the goblet (cylinder + hemisphere) */
Vol_cyl: 22/7*r^2*h;
Vol_hemi: (2/3)*22/7*r^3;
Total_Vol: Vol_cyl + Vol_hemi;
Total_Vol_val1: float(Total_Vol);
Total_Vol_val:round(Total_Vol_val1);
