questionvariables.text:
/* --- 1. SETUP FOR DATA SOURCE (Input 1) --- */
correct_text_ds: "The specific origin, location, or medium from where statistical facts are obtained.";

/* Pool of wrong answers specific to Data Source */
wrongs_ds: [
