questionvariables.text:
/* Random side lengths: smaller between 2 and 8, larger between 9 and 15 */
s1 : rand(7) + 2;        /* 2–8 */
s2 : rand(7) + 9;        /* 9–15 */

g : gcd(s1,s2);
if g > 1 then s2:s2+1 else s2:s2;

/* Random area of small hexagon */
A_small : rand(50) + 10;   /* area between 10 and 59 */

/* Ratio of similarity (linear) */
k : s2/s1;

/* Area ratio */
ratio_area : k^2;

/* Area of larger hexagon */
A_large : A_small * ratio_area;
ta:ratio_area;
ta1:(s1/s2)^2;
ta2:decimalplaces(A_large,2);

/*mistakes for part a*/
/*Giving the ratio of sides instead of areas*/
ta3:s1/s2;
ta4:k;


/*mistakes for part b*/
/*Using the linear scale factor instead of the area scale factor*/
ta5:decimalplaces(A_small * k,2);
/*Reversing the scale factor*/
ta6:decimalplaces((A_small * ta3^2),2);
