questionvariables.text:
/* Generate perfect-square values for a and b */
square_list_a:rand ([4,9,16,25,36,49,64,81,100]);  /* extend if desired */
square_list_b:rand ([121,144,169,196,225,256,289,324,400,441,484,529,576,625]);  /* extend if desired */

/* Area of the larger cone */
c: rand_with_step(700,1200,5);

/* Slant height of the smaller cone */
d: rand(12) + 7;
/*area of the small cone*/
small_cone:float(square_list_a*c)/square_list_b;
/*square root of b/a*/
squareroot:sqrt(square_list_b/square_list_a);
/*Slanting height of the cone*/
slating_height:float(squareroot*d);
ta:decimalplaces(small_cone,2);
ta1:decimalplaces(slating_height,2);

/*mistakes for part a */
/*Reversing the ratio of the areas*/
small_cone1:float(square_list_b*c)/square_list_a;
ta2:decimalplaces(small_cone1,2);
/*Treating the area ratio as a linear ratio*/
squareroot1:sqrt(square_list_a/square_list_b);
ta3:decimalplaces((squareroot1*c),2);

/*mistakes for part b */
/*Using the area ratio directly for slanting height*/
ta4:decimalplaces((square_list_b/square_list_a)*d,2);
/*Using the inverse scale factor*/
ta5:decimalplaces((1/squareroot)*d,2);
