questionvariables.text:
/* (a) Integer divided by a fraction */
a_num : rand(8)+2; /* 2–9 */
a_den : rand_with_prohib(2,9,[a_num]);
frac_a : a_den/a_num; /* gives form like 6 ÷ (2/3) */
ta_a : a_num*(a_num/a_den);
/*mistake*/
mistake_ta1: 1/a_den;


/* (b) Linear equation 4x = 12 type */
b_coeff : rand(8)+2; /* 2–9 */
b_rhs : b_coeff*(rand(6)+2);
ta_b : b_rhs/b_coeff;
mistake_ta2:b_rhs*b_coeff;


/* (c) Fraction times x equals integer */
c_num : rand(4)+1; /* 1–5 */
c_den : rand_with_prohib(2,6,[c_num]);
c_rhs : (rand(6)+2);
ta_c : c_rhs*(c_den/c_num);
mistake_ta3:c_rhs*(c_num/c_den);


/* (d) Decimal division */
d1 : rand(8)+1; /* 1–9 */
d2 : rand_with_prohib(2,11,[d1]); 
dec1 : float(d1/10);
dec2 : float(d2/10);
ta_d : dec1/dec2;
