questionvariables.text:
/* Randomly choose one of three options */
option: rand(3);

if option = 0 then (
    a: 3,
    b: 6,
    ta: 2
) elseif option = 1 then (
    a: 4,
    b: 6,
    ta: 12/5
) else (
    a: 5,
    b: 10,
    ta: 10/3
);

/* Calculate rates */
ta1: 1/a;
ta2: 1/b;
ta3: ta1 + ta2;
/*mistake*/
 ta1_mistake: a;
ta2_mistake: b;
ta3_mistake1: 1/(a+b);
ta3_mistake2: (a+b)/2;
ta4_mistake2: a+b;
